<?php//评论列表function wpmee_comment($comment, $args, $depth) {    global $post;    $commentcountText='';    $GLOBALS['comment'] = $comment;?><li id="li-comment-<?php comment_ID() ?>" class="comment-body comment-parent comment-odd"><div <?php comment_class(); ?> id="li-comment-<?php comment_ID() ?>">	<div class="comment-view" onclick="">		<div class="comment-header"><?php echo get_avatar( $comment,40 ); ?><span class="comment-author"><?php comment_author_link() ?></span><?php if($comment->user_id == 1) echo "<span class='comment_admin'>博主</span>"; ?></div>            <div class="comment-content">            <?php comment_text() ?>            <?php if ( $comment->comment_approved == '0' ) : ?>            <p class="shenhe"><i class="icon-warning-outline"></i>您的评论正在等待审核中...</p>            <?php endif; ?>            </div>		<div class="comment-meta">			<time class="comment-time">评论时间：<?php echo timeago( $comment->comment_date_gmt ); ?></time>			<span class="comment-reply"><?php comment_reply_link(array_merge( $args, array('depth' => $depth, 'max_depth' => $args['max_depth'], 'reply_text' => "回复"))) ?></span>		</div>    </div></div><?php }function wpmee_end_comment() {    echo '</li>';}